import { createTheme } from '@mui/material/styles';

export const purpleTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#A78BFA',      // Soft purple
      light: '#C4B5FD',
      dark: '#7C3AED',
    },
    secondary: {
      main: '#06B6D4',      // Cyan accent
      light: '#22D3EE',
      dark: '#0891B2',
    },
    background: {
      default: '#0F0A1E',   // Deep space purple
      paper: '#1A1333',     // Dark card
    },
    text: {
      primary: '#F3F4F6',
      secondary: '#9CA3AF',
    },
    success: {
      main: '#10B981',
    },
    divider: 'rgba(139, 92, 246, 0.1)',
  },
  typography: {
    fontFamily: '"Inter", "SF Pro Display", -apple-system, sans-serif',
    h4: {
      fontWeight: 700,
      letterSpacing: '-0.02em',
    },
    h6: {
      fontWeight: 500,
      letterSpacing: '-0.01em',
    },
  },
  shape: {
    borderRadius: 12,
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundImage: 'linear-gradient(145deg, #1A1333 0%, #0F0A1E 100%)',
          border: '1px solid rgba(139, 92, 246, 0.2)',
        },
      },
    },
  },
});
