/**
 * Typography Configuration
 * Font families, sizes, and weights
 */

export const typography = {
  fontFamily: '"Inter", "SF Pro Display", -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif',

  h1: {
    fontSize: '3rem',
    fontWeight: 700,
    letterSpacing: '-0.02em',
    lineHeight: 1.2,
  },

  h2: {
    fontSize: '2.5rem',
    fontWeight: 700,
    letterSpacing: '-0.02em',
    lineHeight: 1.3,
  },

  h3: {
    fontSize: '2rem',
    fontWeight: 600,
    letterSpacing: '-0.01em',
    lineHeight: 1.4,
  },

  h4: {
    fontSize: '1.5rem',
    fontWeight: 700,
    letterSpacing: '-0.02em',
    lineHeight: 1.4,
  },

  h5: {
    fontSize: '1.25rem',
    fontWeight: 600,
    letterSpacing: '-0.01em',
    lineHeight: 1.5,
  },

  h6: {
    fontSize: '1rem',
    fontWeight: 500,
    letterSpacing: '-0.01em',
    lineHeight: 1.6,
  },

  body1: {
    fontSize: '0.95rem',
    fontWeight: 400,
    lineHeight: 1.6,
  },

  body2: {
    fontSize: '0.875rem',
    fontWeight: 400,
    lineHeight: 1.5,
  },

  button: {
    fontSize: '0.875rem',
    fontWeight: 500,
    textTransform: 'none',
    letterSpacing: '0.02em',
  },

  caption: {
    fontSize: '0.75rem',
    fontWeight: 400,
    lineHeight: 1.4,
  },
};
