import React, { useState, useEffect } from 'react';
import { Container, Box, Paper, Tabs, Tab } from '@mui/material';
import TaskList from './TaskList';

function TaskDashboard() {
  // Multiple lists!
  const [taskLists, setTaskLists] = useState(() => {
    const saved = localStorage.getItem('neuranote-task-lists');
    if (saved) {
      return JSON.parse(saved);
    }
    return {
      work: {
        id: 'work',
        name: 'ðŸ’¼ Work',
        color: '#9F7AEA',
        tasks: [
          { id: 1, title: 'Complete SRS Document', completed: false },
          { id: 2, title: 'Design database schema', completed: false },
        ]
      },
      study: {
        id: 'study',
        name: 'ðŸ“š Study',
        color: '#48BB78',
        tasks: [
          { id: 3, title: 'Prepare interim demo', completed: true },
          { id: 4, title: 'Read React documentation', completed: false },
        ]
      },
      personal: {
        id: 'personal',
        name: 'ðŸŒŸ Personal',
        color: '#ED8936',
        tasks: [
          { id: 5, title: 'Buy groceries', completed: false },
        ]
      }
    };
  });

  const [activeList, setActiveList] = useState('work');

  // Save to localStorage
  useEffect(() => {
    localStorage.setItem('neuranote-task-lists', JSON.stringify(taskLists));
  }, [taskLists]);

  const handleToggle = (taskId) => {
    setTaskLists(prev => ({
      ...prev,
      [activeList]: {
        ...prev[activeList],
        tasks: prev[activeList].tasks.map(t =>
          t.id === taskId ? { ...t, completed: !t.completed } : t
        )
      }
    }));
  };

  const handleAdd = (title) => {
    const newTask = {
      id: Date.now(),
      title,
      completed: false
    };
    setTaskLists(prev => ({
      ...prev,
      [activeList]: {
        ...prev[activeList],
        tasks: [...prev[activeList].tasks, newTask]
      }
    }));
  };

  const handleDelete = (taskId) => {
    setTaskLists(prev => ({
      ...prev,
      [activeList]: {
        ...prev[activeList],
        tasks: prev[activeList].tasks.filter(t => t.id !== taskId)
      }
    }));
  };

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 3 }}>
      <Container maxWidth="md">
        <Paper sx={{ p: 4, bgcolor: 'background.paper', borderRadius: 2 }}>

          {/* Tabs for switching lists */}
          <Tabs 
            value={activeList} 
            onChange={(e, newValue) => setActiveList(newValue)}
            sx={{ 
              mb: 3,
              borderBottom: 1,
              borderColor: 'divider',
              '& .MuiTab-root': {
                color: 'text.secondary',
                '&.Mui-selected': {
                  color: 'primary.main'
                }
              }
            }}
          >
            {Object.values(taskLists).map(list => (
              <Tab 
                key={list.id} 
                value={list.id} 
                label={list.name}
              />
            ))}
          </Tabs>

          {/* Current list */}
          <TaskList
            listName={taskLists[activeList].name}
            tasks={taskLists[activeList].tasks}
            onToggle={handleToggle}
            onAdd={handleAdd}
            onDelete={handleDelete}
          />
        </Paper>
      </Container>
    </Box>
  );
}

export default TaskDashboard;
