/**
 * Green Olive Component Overrides
 */

export const components = {
  MuiPaper: {
    styleOverrides: {
      root: {
        backgroundImage: 'none',
        backgroundColor: '#FFFFFF',
        border: '1px solid rgba(107, 123, 63, 0.12)',
        boxShadow: '0 4px 20px rgba(107, 123, 63, 0.08)',
      },
    },
  },

  MuiButton: {
    styleOverrides: {
      root: {
        textTransform: 'none',
        fontWeight: 500,
        borderRadius: 8,
        padding: '10px 20px',
        transition: 'all 0.2s',
      },
      contained: {
        boxShadow: 'none',
        '&:hover': {
          boxShadow: '0 4px 12px rgba(107, 123, 63, 0.25)',
          transform: 'translateY(-1px)',
        },
      },
    },
  },

  MuiTabs: {
    styleOverrides: {
      root: {
        minHeight: 48,
        borderBottom: '1px solid rgba(107, 123, 63, 0.15)',
      },
      indicator: {
        height: 3,
        borderRadius: '3px 3px 0 0',
        backgroundColor: '#6B7B3F',
      },
    },
  },

  MuiTab: {
    styleOverrides: {
      root: {
        textTransform: 'none',
        fontSize: '0.95rem',
        fontWeight: 500,
        minHeight: 48,
        color: '#5A6B47',
        transition: 'all 0.2s',
        '&:hover': {
          color: '#6B7B3F',
          backgroundColor: 'rgba(107, 123, 63, 0.05)',
        },
        '&.Mui-selected': {
          color: '#6B7B3F',
          fontWeight: 600,
        },
      },
    },
  },

  MuiTextField: {
    styleOverrides: {
      root: {
        '& .MuiOutlinedInput-root': {
          borderRadius: 12,
          backgroundColor: '#FAFBF8',
          transition: 'all 0.2s',
        },
      },
    },
  },

  MuiCheckbox: {
    styleOverrides: {
      root: {
        borderRadius: 4,
        transition: 'all 0.2s',
        color: '#6B7B3F',
        '&:hover': {
          backgroundColor: 'rgba(107, 123, 63, 0.06)',
        },
      },
    },
  },

  MuiChip: {
    styleOverrides: {
      root: {
        fontWeight: 600,
        borderRadius: 6,
      },
    },
  },

  MuiIconButton: {
    styleOverrides: {
      root: {
        transition: 'all 0.2s',
        '&:hover': {
          transform: 'scale(1.05)',
        },
      },
    },
  },

  MuiLinearProgress: {
    styleOverrides: {
      root: {
        borderRadius: 6,
        backgroundColor: 'rgba(107, 123, 63, 0.12)',
      },
      bar: {
        borderRadius: 6,
        background: 'linear-gradient(90deg, #6B7B3F, #8B9B5F)',
      },
    },
  },
};
