import React, { useState } from 'react';
import { Box, Typography, TextField, Chip } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import TaskItem from './TaskItem';

function TaskList({ listName, tasks, onToggle, onAdd, onDelete }) {
  const [newTask, setNewTask] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newTask.trim()) {
      onAdd(newTask);
      setNewTask('');
    }
  };

  // Count stats
  const completedCount = tasks.filter(t => t.completed).length;
  const totalCount = tasks.length;

  return (
    <Box>
      {/* Header with list name */}
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
        <Typography variant="h4">
          Good Afternoon.
        </Typography>
        <Chip 
          label={`${completedCount}/${totalCount}`}
          size="small"
          sx={{ 
            bgcolor: 'primary.main',
            color: 'white',
            fontWeight: 'bold'
          }}
        />
      </Box>

      <Typography variant="h6" sx={{ mb: 3, color: 'text.secondary' }}>
        What's your plan for {listName.replace(/[^\w\s]/gi, '')}?
      </Typography>

      {/* Add Task Input */}
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          placeholder={`Add task to ${listName}`}
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          sx={{ mb: 3 }}
          InputProps={{
            startAdornment: <AddIcon sx={{ mr: 1, color: 'primary.main' }} />
          }}
        />
      </form>

      {/* Task List */}
      {tasks.length === 0 ? (
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <Typography color="text.secondary">
            No tasks in {listName} yet. Add one above! ðŸš€
          </Typography>
        </Box>
      ) : (
        tasks.map(task => (
          <TaskItem
            key={task.id}
            task={task}
            onToggle={onToggle}
            onDelete={onDelete}
          />
        ))
      )}
    </Box>
  );
}

export default TaskList;
