import React, { useState, useEffect } from 'react';
import { Container, Box, Paper } from '@mui/material';
import TaskList from './TaskList';

function TaskDashboard() {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('neuranote-tasks');
    if (savedTasks) {
      return JSON.parse(savedTasks);
    }
    return [
      { id: 1, title: 'Complete SRS Document', completed: false },
      { id: 2, title: 'Design database schema', completed: false },
      { id: 3, title: 'Prepare interim demo', completed: true },
    ];
  });

  useEffect(() => {
    localStorage.setItem('neuranote-tasks', JSON.stringify(tasks));
  }, [tasks]);

  const handleToggle = (taskId) => {
    setTasks(tasks.map(t => 
      t.id === taskId ? { ...t, completed: !t.completed } : t
    ));
  };

  const handleAdd = (title) => {
    setTasks([...tasks, {
      id: Date.now(),
      title,
      completed: false
    }]);
  };

  const handleDelete = (taskId) => {
    setTasks(tasks.filter(t => t.id !== taskId));
  };

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', p: 3 }}>
      <Container maxWidth="md">
        <Paper sx={{ p: 4, bgcolor: 'background.paper', borderRadius: 2 }}>
          <TaskList
            tasks={tasks}
            onToggle={handleToggle}
            onAdd={handleAdd}
            onDelete={handleDelete}
          />
        </Paper>
      </Container>
    </Box>
  );
}

export default TaskDashboard;
